<template lang="pug">
  div.loading
    tb-header(:title="$t('common.title.loading')", ref="header", style="background: white;")
    .flex.body.flex-down
      img(src="data:image/gif;base64,")
      mt-progress.progress(:value='percent * 100', :bar-height='5')
        .percent(slot="end") {{percent | tbPercent}}

</template>

<script>
import { each } from 'lodash'

const images = [
  require('@/assets/images/buttons-retina.png'),
  require('@/assets/images/coin_bg@3x.png'),
  require('@/assets/images/ic_default_doll_icon.png'),
  require('@/assets/images/ic_empty_tick.png'),
  require('@/assets/images/icon_user.png'),
  require('@/assets/images/icons-retina.png'),
  require('@/assets/images/profile_bg@3x.png'),
  require('@/assets/images/push-coin.wav'),
  require('@/assets/images/bullet-fill.wav')
]

export default {
  mounted() {
    this.redirect = decodeURIComponent(this.$route.query.redirect || '')
    this.loadingImages()
  },

  methods: {
    loadingImages() {
      const step = 1 / images.length
      const promises = []
      each(images, image => {
        promises.push(new Promise((resolve, reject) => {
          const img = new Image()
          img.onerror = () => {
            console.warn('资源加载失败：', img)
            this.percent += step
            resolve()
          }
          img.onload = () => {
            this.percent += step
            resolve()
          }
          img.src = image
        }))
      })

      Promise.all(promises).then(res => {
        console.log('loading done')
        this.$store.commit('updateLoadingSuccess', true)
        this.$router.replace({
          path: this.redirect || '/h5/index'
        })
      })
    }
  },

  data() {
    return {
      redirect: '',
      percent: 0
    }
  }
}
</script>

<style lang="scss" scoped>
.body {
  padding-top: 20vh
}

.progress {
  width: 40vw;
  margin-top: 10px;
}

img {
  max-height: 30vh;
}
</style>
